function convertHtmlEntities(text) {
    var entityMap = {
        'amp': '&',
        'lt': '<',
        'gt': '>',
        'quot': '"',
        '#39': "'",
        '#x2F': '/',
        '#x60': '`',
        '#x3D': '=',
    };

    return text.replace(/[&]([^;]+);/g, function(e,m) {
        return entityMap[m] || e;
    });
}

